const initialState = {
  count: 0,
  sagaCount: 0
};

export const INCREMENT = "INCREMENT";
export const DECREMENT = "DECREMENT";
export const INCREMENT_ASYNC = "INCREMENT_ASYNC";
export const DECREMENT_ASYNC = "DECREMENT_ASYNC";

// リデューサー
export default function Reducer(state = initialState, action) {
  switch (action.type) {
    case INCREMENT:
      return incrementReduce(state, action);
    case DECREMENT:
      return decrementReduce(state, action);
    case INCREMENT_ASYNC:
      return incrementReduce(state, action);
    case DECREMENT_ASYNC:
      return decrementReduce(state, action);
    default:
      return state;
  }
};

const incrementReduce = (state, action) => {
  if (action.type === INCREMENT) {
    state.count++;
  }
  else {
    state.sagaCount++;
  }

  return state;
};

const decrementReduce = (state, action) => {
  return { count: state.count - 1 };
};

//アクションクリエーター
export const actionIncrement = props => {
  return {
    type: props.type ? INCREMENT_ASYNC : props.type
  };
};

export const actionDecrement = props => {
  return {
    type: props.type ? DECREMENT : props.type
  };
};

export const actionIncrementAsync = props => {
  return {
    type: INCREMENT_ASYNC
  };
};

export const actionDecrementAsync = props => {
  return {
    type: DECREMENT_ASYNC
  };
};
