import { createStore, applyMiddleware } from "redux";
import CreateSagaMiddleWare from "redux-saga";
import RootSaga from './Saga';

const initData = {
  count: 0
};

// リデューサー
export const countReducer = (state = initData, action) => {
  switch (action.type) {
    case "INCREMENT":
      return incrementReduce(state, action);
    case "DECREMENT":
      return decrementReduce(state, action);
    default:
      return state;
  }
};

const incrementReduce = (state, action) => {
  return { count: state.count + 1 };
};

const decrementReduce = (state, action) => {
  return { count: state.count - 1 };
};

//アクションクリエーター
export const increment = props => {
  return {
    type: "INCREMENT"
  };
};

export const decrement = props => {
  return {
    type: "DECREMENT"
  };
};

//ストア作成
const sagaMiddleware = CreateSagaMiddleWare();
export default createStore(countReducer, applyMiddleware(sagaMiddleware));
sagaMiddleware.run(RootSaga);
