import { createStore } from "redux";

const initData = {
  count: 0
};

export const countReducer = (state = initData, action) => {
  switch (action.type) {
    case "INCREMENT":
      return incrementReduce(state, action);
    case "DECREMENT":
      return decrementReduce(state, action);
    default:
      return state;
  }
};

const incrementReduce = (state, action) => {
  return { count: state.count + 1 };
};

const decrementReduce = (state, action) => {
  return { count: state.count - 1 };
};

//アクションクリエーター
export const increment = props => {
  return {
    type: "INCREMENT"
  };
};

export const decrement = props => {
  return {
    type: "DECREMENT"
  };
};

//ストア作成
export default createStore(countReducer);
